input.content:
name: Project Automation

on:
  issues:
    types: [opened, closed, reopened, labeled, unlabeled, assigned]
  pull_request:
    types: [opened, closed, merged, ready_for_review, converted_to_draft]
  issue_comment:
    types: [created]
  workflow_dispatch:
    inputs:
      phase:
        description: 'Phase to transition to'
        required: true
        type: choice
        options:
          - 'phase-1-planning'
          - 'phase-2-architecture'
          - 'phase-3-setup'
          - 'phase-4-core-development'
          - 'phase-5-integration'
          - 'phase-6-testing'
          - 'phase-7-optimization'
          - 'phase-8-documentation'
          - 'phase-9-deployment'
          - 'phase-10-monitoring'
          - 'phase-11-maintenance'
          - 'phase-12-review'

permissions:
  issues: write
  pull-requests: write
  repository-projects: write
  contents: read

jobs:
  auto-triage:
    runs-on: ubuntu-latest
    if: github.event_name == 'issues' && github.event.action == 'opened'

    steps:
      - name: Auto-label new issues
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;
            const labels = [];

            // Determine phase based on issue title or body
            const content = (issue.title + ' ' + issue.body).toLowerCase();

            // Phase detection
            if (content.includes('phase 1') || content.includes('planning')) {
              labels.push('phase-1-planning');
            } else if (content.includes('phase 2') || content.includes('architecture')) {
              labels.push('phase-2-architecture');
            } else if (content.includes('phase 3') || content.includes('setup')) {
              labels.push('phase-3-setup');
            } else if (content.includes('phase 4') || content.includes('core')) {
              labels.push('phase-4-core-development');
            } else if (content.includes('phase 5') || content.includes('integration')) {
              labels.push('phase-5-integration');
            } else if (content.includes('phase 6') || content.includes('testing')) {
              labels.push('phase-6-testing');
            } else if (content.includes('phase 7') || content.includes('optimization')) {
              labels.push('phase-7-optimization');
            } else if (content.includes('phase 8') || content.includes('documentation')) {
              labels.push('phase-8-documentation');
            } else if (content.includes('phase 9') || content.includes('deployment')) {
              labels.push('phase-9-deployment');
            } else if (content.includes('phase 10') || content.includes('monitoring')) {
              labels.push('phase-10-monitoring');
            } else if (content.includes('phase 11') || content.includes('maintenance')) {
              labels.push('phase-11-maintenance');
            } else if (content.includes('phase 12') || content.includes('review')) {
              labels.push('phase-12-review');
            }

            // Priority detection
            if (content.includes('urgent') || content.includes('critical')) {
              labels.push('priority-high');
            } else if (content.includes('important')) {
              labels.push('priority-medium');
            } else {
              labels.push('priority-low');
            }

            // Type detection
            if (content.includes('bug') || content.includes('error') || content.includes('fix')) {
              labels.push('bug');
            } else if (content.includes('feature') || content.includes('enhancement')) {
              labels.push('enhancement');
            } else if (content.includes('docs') || content.includes('documentation')) {
              labels.push('documentation');
            }

            // Add status label
            labels.push('status-todo');

            // Apply labels
            if (labels.length > 0) {
              await github.rest.issues.addLabels({
                issue_number: issue.number,
                owner: context.repo.owner,
                repo: context.repo.repo,
                labels: labels
              });
            }

  track-phase-progress:
    runs-on: ubuntu-latest
    if: github.event_name == 'issues' && github.event.action == 'closed'

    steps:
      - name: Update phase progress
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;

            // Get all issues to calculate progress
            const allIssues = await github.paginate(github.rest.issues.listForRepo, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Group by phase
            const phases = {};
            for (let i = 1; i <= 12; i++) {
              phases[`phase-${i}`] = { total: 0, closed: 0 };
            }

            allIssues.forEach(issue => {
              issue.labels.forEach(label => {
                if (label.name.startsWith('phase-')) {
                  const phaseNum = label.name.match(/phase-(\d+)/)[1];
                  phases[`phase-${phaseNum}`].total++;
                  if (issue.state === 'closed') {
                    phases[`phase-${phaseNum}`].closed++;
                  }
                }
              });
            });

            // Create progress report
            let progressReport = '## Build Plan Progress Report\n\n';
            progressReport += '| Phase | Progress | Status |\n';
            progressReport += '|-------|----------|--------|\n';

            const phaseNames = [
              'Planning', 'Architecture', 'Setup', 'Core Development',
              'Integration', 'Testing', 'Optimization', 'Documentation',
              'Deployment', 'Monitoring', 'Maintenance', 'Review'
            ];

            for (let i = 1; i <= 12; i++) {
              const phase = phases[`phase-${i}`];
              const percentage = phase.total > 0 ? Math.round((phase.closed / phase.total) * 100) : 0;
              const status = percentage === 100 ? '✅ Complete' : 
                            percentage > 50 ? '🔄 In Progress' : 
                            percentage > 0 ? '🟡 Started' : '⏳ Not Started';

              progressReport += `| Phase ${i}: ${phaseNames[i-1]} | ${phase.closed}/${phase.total} (${percentage}%) | ${status} |\n`;
            }

            // Find or create progress tracking issue
            const progressIssues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              labels: 'progress-tracking',
              state: 'open'
            });

            if (progressIssues.data.length > 0) {
              // Update existing progress issue
              await github.rest.issues.update({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: progressIssues.data[0].number,
                body: progressReport + '\n\n*Last updated: ' + new Date().toISOString() + '*'
              });
            }

  phase-transition:
    runs-on: ubuntu-latest
    if: github.event_name == 'workflow_dispatch'

    steps:
      - name: Transition to new phase
        uses: actions/github-script@v7
        with:
          script: |
            const targetPhase = '${{ github.event.inputs.phase }}';
            const phaseNum = parseInt(targetPhase.match(/phase-(\d+)/)[1]);

            // Create milestone for the phase if not exists
            const milestones = await github.rest.issues.listMilestones({
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'open'
            });

            let milestone = milestones.data.find(m => m.title === `Phase ${phaseNum}`);
            if (!milestone) {
              const result = await github.rest.issues.createMilestone({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: `Phase ${phaseNum}`,
                description: `Milestone for ${targetPhase}`,
                due_on: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString()
              });
              milestone = result.data;
            }

            // Update all issues in this phase to use the milestone
            const phaseIssues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              labels: targetPhase,
              state: 'open'
            });

            for (const issue of phaseIssues.data) {
              await github.rest.issues.update({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issue.number,
                milestone: milestone.number
              });
            }

            // Create phase kickoff issue
            await github.rest.issues.create({
              owner: context.repo.owner,
              repo: context.repo.repo,
              title: `🚀 Phase ${phaseNum} Kickoff`,
              body: `## Phase ${phaseNum} has begun!\n\n### Objectives\n- [ ] Complete all issues labeled with \`${targetPhase}\`\n- [ ] Review and test all changes\n- [ ] Update documentation\n\n### Team\ncc @Beaulewis1977\n\n### Timeline\nTarget completion: ${new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toLocaleDateString()}`,
              labels: [targetPhase, 'phase-kickoff'],
              milestone: milestone.number
            });

  command-processor:
    runs-on: ubuntu-latest
    if: github.event_name == 'issue_comment' && github.event.issue.pull_request == null

    steps:
      - name: Process slash commands
        uses: actions/github-script@v7
        with:
          script: |
            const comment = context.payload.comment.body;
            const issue = context.payload.issue;

            // Status update commands
            if (comment.startsWith('/status')) {
              const status = comment.split(' ')[1];
              const validStatuses = ['todo', 'in-progress', 'blocked', 'review', 'done'];

              if (validStatuses.includes(status)) {
                // Remove old status labels
                const currentLabels = issue.labels.filter(l => !l.name.startsWith('status-'));

                // Add new status label
                await github.rest.issues.setLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  labels: [...currentLabels.map(l => l.name), `status-${status}`]
                });

                // Add reaction to confirm
                await github.rest.reactions.createForIssueComment({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  comment_id: context.payload.comment.id,
                  content: '+1'
                });
              }
            }

            // Phase assignment
            if (comment.startsWith('/phase')) {
              const phaseNum = comment.match(/\/phase (\d+)/)?.[1];
              if (phaseNum && phaseNum >= 1 && phaseNum <= 12) {
                const phaseLabel = `phase-${phaseNum}`;

                // Remove other phase labels
                const currentLabels = issue.labels.filter(l => !l.name.startsWith('phase-'));

                // Add new phase label
                await github.rest.issues.setLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  labels: [...currentLabels.map(l => l.name), phaseLabel]
                });

                await github.rest.reactions.createForIssueComment({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  comment_id: context.payload.comment.id,
                  content: 'rocket'
                });
              }
            }

            // Priority update
            if (comment.startsWith('/priority')) {
              const priority = comment.split(' ')[1];
              const validPriorities = ['low', 'medium', 'high', 'critical'];

              if (validPriorities.includes(priority)) {
                // Remove old priority labels
                const currentLabels = issue.labels.filter(l => !l.name.startsWith('priority-'));

                // Add new priority label
                await github.rest.issues.setLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  labels: [...currentLabels.map(l => l.name), `priority-${priority}`]
                });

                await github.rest.reactions.createForIssueComment({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  comment_id: context.payload.comment.id,
                  content: 'eyes'
                });
              }
            }

toolUseResult.content:
name: Project Automation

on:
  issues:
    types: [opened, closed, reopened, labeled, unlabeled, assigned]
  pull_request:
    types: [opened, closed, merged, ready_for_review, converted_to_draft]
  issue_comment:
    types: [created]
  workflow_dispatch:
    inputs:
      phase:
        description: 'Phase to transition to'
        required: true
        type: choice
        options:
          - 'phase-1-planning'
          - 'phase-2-architecture'
          - 'phase-3-setup'
          - 'phase-4-core-development'
          - 'phase-5-integration'
          - 'phase-6-testing'
          - 'phase-7-optimization'
          - 'phase-8-documentation'
          - 'phase-9-deployment'
          - 'phase-10-monitoring'
          - 'phase-11-maintenance'
          - 'phase-12-review'

permissions:
  issues: write
  pull-requests: write
  repository-projects: write
  contents: read

jobs:
  auto-triage:
    runs-on: ubuntu-latest
    if: github.event_name == 'issues' && github.event.action == 'opened'

    steps:
      - name: Auto-label new issues
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;
            const labels = [];

            // Determine phase based on issue title or body
            const content = (issue.title + ' ' + issue.body).toLowerCase();

            // Phase detection
            if (content.includes('phase 1') || content.includes('planning')) {
              labels.push('phase-1-planning');
            } else if (content.includes('phase 2') || content.includes('architecture')) {
              labels.push('phase-2-architecture');
            } else if (content.includes('phase 3') || content.includes('setup')) {
              labels.push('phase-3-setup');
            } else if (content.includes('phase 4') || content.includes('core')) {
              labels.push('phase-4-core-development');
            } else if (content.includes('phase 5') || content.includes('integration')) {
              labels.push('phase-5-integration');
            } else if (content.includes('phase 6') || content.includes('testing')) {
              labels.push('phase-6-testing');
            } else if (content.includes('phase 7') || content.includes('optimization')) {
              labels.push('phase-7-optimization');
            } else if (content.includes('phase 8') || content.includes('documentation')) {
              labels.push('phase-8-documentation');
            } else if (content.includes('phase 9') || content.includes('deployment')) {
              labels.push('phase-9-deployment');
            } else if (content.includes('phase 10') || content.includes('monitoring')) {
              labels.push('phase-10-monitoring');
            } else if (content.includes('phase 11') || content.includes('maintenance')) {
              labels.push('phase-11-maintenance');
            } else if (content.includes('phase 12') || content.includes('review')) {
              labels.push('phase-12-review');
            }

            // Priority detection
            if (content.includes('urgent') || content.includes('critical')) {
              labels.push('priority-high');
            } else if (content.includes('important')) {
              labels.push('priority-medium');
            } else {
              labels.push('priority-low');
            }

            // Type detection
            if (content.includes('bug') || content.includes('error') || content.includes('fix')) {
              labels.push('bug');
            } else if (content.includes('feature') || content.includes('enhancement')) {
              labels.push('enhancement');
            } else if (content.includes('docs') || content.includes('documentation')) {
              labels.push('documentation');
            }

            // Add status label
            labels.push('status-todo');

            // Apply labels
            if (labels.length > 0) {
              await github.rest.issues.addLabels({
                issue_number: issue.number,
                owner: context.repo.owner,
                repo: context.repo.repo,
                labels: labels
              });
            }

  track-phase-progress:
    runs-on: ubuntu-latest
    if: github.event_name == 'issues' && github.event.action == 'closed'

    steps:
      - name: Update phase progress
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;

            // Get all issues to calculate progress
            const allIssues = await github.paginate(github.rest.issues.listForRepo, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Group by phase
            const phases = {};
            for (let i = 1; i <= 12; i++) {
              phases[`phase-${i}`] = { total: 0, closed: 0 };
            }

            allIssues.forEach(issue => {
              issue.labels.forEach(label => {
                if (label.name.startsWith('phase-')) {
                  const phaseNum = label.name.match(/phase-(\d+)/)[1];
                  phases[`phase-${phaseNum}`].total++;
                  if (issue.state === 'closed') {
                    phases[`phase-${phaseNum}`].closed++;
                  }
                }
              });
            });

            // Create progress report
            let progressReport = '## Build Plan Progress Report\n\n';
            progressReport += '| Phase | Progress | Status |\n';
            progressReport += '|-------|----------|--------|\n';

            const phaseNames = [
              'Planning', 'Architecture', 'Setup', 'Core Development',
              'Integration', 'Testing', 'Optimization', 'Documentation',
              'Deployment', 'Monitoring', 'Maintenance', 'Review'
            ];

            for (let i = 1; i <= 12; i++) {
              const phase = phases[`phase-${i}`];
              const percentage = phase.total > 0 ? Math.round((phase.closed / phase.total) * 100) : 0;
              const status = percentage === 100 ? '✅ Complete' : 
                            percentage > 50 ? '🔄 In Progress' : 
                            percentage > 0 ? '🟡 Started' : '⏳ Not Started';

              progressReport += `| Phase ${i}: ${phaseNames[i-1]} | ${phase.closed}/${phase.total} (${percentage}%) | ${status} |\n`;
            }

            // Find or create progress tracking issue
            const progressIssues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              labels: 'progress-tracking',
              state: 'open'
            });

            if (progressIssues.data.length > 0) {
              // Update existing progress issue
              await github.rest.issues.update({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: progressIssues.data[0].number,
                body: progressReport + '\n\n*Last updated: ' + new Date().toISOString() + '*'
              });
            }

  phase-transition:
    runs-on: ubuntu-latest
    if: github.event_name == 'workflow_dispatch'

    steps:
      - name: Transition to new phase
        uses: actions/github-script@v7
        with:
          script: |
            const targetPhase = '${{ github.event.inputs.phase }}';
            const phaseNum = parseInt(targetPhase.match(/phase-(\d+)/)[1]);

            // Create milestone for the phase if not exists
            const milestones = await github.rest.issues.listMilestones({
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'open'
            });

            let milestone = milestones.data.find(m => m.title === `Phase ${phaseNum}`);
            if (!milestone) {
              const result = await github.rest.issues.createMilestone({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: `Phase ${phaseNum}`,
                description: `Milestone for ${targetPhase}`,
                due_on: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString()
              });
              milestone = result.data;
            }

            // Update all issues in this phase to use the milestone
            const phaseIssues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              labels: targetPhase,
              state: 'open'
            });

            for (const issue of phaseIssues.data) {
              await github.rest.issues.update({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issue.number,
                milestone: milestone.number
              });
            }

            // Create phase kickoff issue
            await github.rest.issues.create({
              owner: context.repo.owner,
              repo: context.repo.repo,
              title: `🚀 Phase ${phaseNum} Kickoff`,
              body: `## Phase ${phaseNum} has begun!\n\n### Objectives\n- [ ] Complete all issues labeled with \`${targetPhase}\`\n- [ ] Review and test all changes\n- [ ] Update documentation\n\n### Team\ncc @Beaulewis1977\n\n### Timeline\nTarget completion: ${new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toLocaleDateString()}`,
              labels: [targetPhase, 'phase-kickoff'],
              milestone: milestone.number
            });

  command-processor:
    runs-on: ubuntu-latest
    if: github.event_name == 'issue_comment' && github.event.issue.pull_request == null

    steps:
      - name: Process slash commands
        uses: actions/github-script@v7
        with:
          script: |
            const comment = context.payload.comment.body;
            const issue = context.payload.issue;

            // Status update commands
            if (comment.startsWith('/status')) {
              const status = comment.split(' ')[1];
              const validStatuses = ['todo', 'in-progress', 'blocked', 'review', 'done'];

              if (validStatuses.includes(status)) {
                // Remove old status labels
                const currentLabels = issue.labels.filter(l => !l.name.startsWith('status-'));

                // Add new status label
                await github.rest.issues.setLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  labels: [...currentLabels.map(l => l.name), `status-${status}`]
                });

                // Add reaction to confirm
                await github.rest.reactions.createForIssueComment({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  comment_id: context.payload.comment.id,
                  content: '+1'
                });
              }
            }

            // Phase assignment
            if (comment.startsWith('/phase')) {
              const phaseNum = comment.match(/\/phase (\d+)/)?.[1];
              if (phaseNum && phaseNum >= 1 && phaseNum <= 12) {
                const phaseLabel = `phase-${phaseNum}`;

                // Remove other phase labels
                const currentLabels = issue.labels.filter(l => !l.name.startsWith('phase-'));

                // Add new phase label
                await github.rest.issues.setLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  labels: [...currentLabels.map(l => l.name), phaseLabel]
                });

                await github.rest.reactions.createForIssueComment({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  comment_id: context.payload.comment.id,
                  content: 'rocket'
                });
              }
            }

            // Priority update
            if (comment.startsWith('/priority')) {
              const priority = comment.split(' ')[1];
              const validPriorities = ['low', 'medium', 'high', 'critical'];

              if (validPriorities.includes(priority)) {
                // Remove old priority labels
                const currentLabels = issue.labels.filter(l => !l.name.startsWith('priority-'));

                // Add new priority label
                await github.rest.issues.setLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  labels: [...currentLabels.map(l => l.name), `priority-${priority}`]
                });

                await github.rest.reactions.createForIssueComment({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  comment_id: context.payload.comment.id,
                  content: 'eyes'
                });
              }
            }

input.content:
name: Phase Reports and Analytics

on:
  schedule:
    - cron: '0 9 * * 1'  # Weekly on Monday at 9 AM UTC
  workflow_dispatch:
    inputs:
      report_type:
        description: 'Type of report to generate'
        required: true
        type: choice
        options:
          - 'phase-summary'
          - 'burndown-chart'
          - 'dependency-map'
          - 'risk-assessment'
          - 'team-velocity'

permissions:
  contents: write
  issues: write
  pull-requests: read

jobs:
  generate-reports:
    runs-on: ubuntu-latest

    steps:
      - name: Checkout code
        uses: actions/checkout@v4

      - name: Setup Node.js
        uses: actions/setup-node@v4
        with:
          node-version: '20'

      - name: Generate Phase Summary Report
        uses: actions/github-script@v7
        with:
          script: |
            const fs = require('fs');

            // Fetch all issues
            const allIssues = await github.paginate(github.rest.issues.listForRepo, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Fetch all PRs
            const allPRs = await github.paginate(github.rest.pulls.list, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Initialize phase data
            const phases = {};
            const phaseNames = [
              'Planning', 'Architecture', 'Setup', 'Core Development',
              'Integration', 'Testing', 'Optimization', 'Documentation',
              'Deployment', 'Monitoring', 'Maintenance', 'Review'
            ];

            for (let i = 1; i <= 12; i++) {
              phases[i] = {
                name: phaseNames[i-1],
                issues: { total: 0, open: 0, closed: 0 },
                prs: { total: 0, open: 0, merged: 0 },
                blockers: [],
                risks: [],
                contributors: new Set(),
                startDate: null,
                endDate: null,
                duration: 0
              };
            }

            // Process issues
            allIssues.forEach(issue => {
              issue.labels.forEach(label => {
                if (label.name.startsWith('phase-')) {
                  const phaseNum = parseInt(label.name.match(/phase-(\d+)/)[1]);
                  phases[phaseNum].issues.total++;

                  if (issue.state === 'open') {
                    phases[phaseNum].issues.open++;

                    // Check for blockers
                    if (issue.labels.some(l => l.name === 'status-blocked')) {
                      phases[phaseNum].blockers.push({
                        number: issue.number,
                        title: issue.title,
                        url: issue.html_url
                      });
                    }

                    // Check for high priority issues (risks)
                    if (issue.labels.some(l => l.name === 'priority-high' || l.name === 'priority-critical')) {
                      phases[phaseNum].risks.push({
                        number: issue.number,
                        title: issue.title,
                        priority: issue.labels.find(l => l.name.startsWith('priority-')).name,
                        url: issue.html_url
                      });
                    }
                  } else {
                    phases[phaseNum].issues.closed++;
                  }

                  // Track contributors
                  if (issue.assignees) {
                    issue.assignees.forEach(assignee => {
                      phases[phaseNum].contributors.add(assignee.login);
                    });
                  }

                  // Track dates
                  const issueDate = new Date(issue.created_at);
                  if (!phases[phaseNum].startDate || issueDate < phases[phaseNum].startDate) {
                    phases[phaseNum].startDate = issueDate;
                  }
                  if (issue.closed_at) {
                    const closeDate = new Date(issue.closed_at);
                    if (!phases[phaseNum].endDate || closeDate > phases[phaseNum].endDate) {
                      phases[phaseNum].endDate = closeDate;
                    }
                  }
                }
              });
            });

            // Process PRs
            allPRs.forEach(pr => {
              pr.labels.forEach(label => {
                if (label.name.startsWith('phase-')) {
                  const phaseNum = parseInt(label.name.match(/phase-(\d+)/)[1]);
                  phases[phaseNum].prs.total++;

                  if (pr.state === 'open') {
                    phases[phaseNum].prs.open++;
                  } else if (pr.merged_at) {
                    phases[phaseNum].prs.merged++;
                  }
                }
              });
            });

            // Generate markdown report
            let report = '# 📊 Master Workflow Build Plan - Phase Report\n\n';
            report += `*Generated: ${new Date().toLocaleString()}*\n\n`;
            report += '## Executive Summary\n\n';

            // Calculate overall progress
            let totalIssues = 0, closedIssues = 0;
            let currentPhase = 0;

            Object.keys(phases).forEach(phaseNum => {
              const phase = phases[phaseNum];
              totalIssues += phase.issues.total;
              closedIssues += phase.issues.closed;

              if (phase.issues.total > 0 && phase.issues.closed < phase.issues.total) {
                if (currentPhase === 0) currentPhase = phaseNum;
              }
            });

            const overallProgress = totalIssues > 0 ? Math.round((closedIssues / totalIssues) * 100) : 0;

            report += `- **Overall Progress**: ${closedIssues}/${totalIssues} issues (${overallProgress}%)\n`;
            report += `- **Current Phase**: Phase ${currentPhase || 12} - ${phases[currentPhase || 12].name}\n`;
            report += `- **Active Contributors**: ${new Set([...Object.values(phases).flatMap(p => Array.from(p.contributors))]).size}\n\n`;

            // Risk summary
            const allRisks = Object.values(phases).flatMap(p => p.risks);
            const allBlockers = Object.values(phases).flatMap(p => p.blockers);

            if (allRisks.length > 0 || allBlockers.length > 0) {
              report += '## ⚠️ Risks and Blockers\n\n';

              if (allBlockers.length > 0) {
                report += '### 🚧 Current Blockers\n';
                allBlockers.forEach(blocker => {
                  report += `- [#${blocker.number}](${blocker.url}): ${blocker.title}\n`;
                });
                report += '\n';
              }

              if (allRisks.length > 0) {
                report += '### 🔴 High Priority Issues\n';
                allRisks.forEach(risk => {
                  report += `- [#${risk.number}](${risk.url}): ${risk.title} (${risk.priority})\n`;
                });
                report += '\n';
              }
            }

            // Detailed phase breakdown
            report += '## Phase Details\n\n';

            Object.keys(phases).forEach(phaseNum => {
              const phase = phases[phaseNum];
              const progress = phase.issues.total > 0 ? Math.round((phase.issues.closed / phase.issues.total) * 100) : 0;
              const status = progress === 100 ? '✅' : progress > 50 ? '🔄' : progress > 0 ? '🟡' : '⏳';

              report += `### ${status} Phase ${phaseNum}: ${phase.name}\n\n`;
              report += `- **Progress**: ${phase.issues.closed}/${phase.issues.total} issues (${progress}%)\n`;
              report += `- **Pull Requests**: ${phase.prs.merged} merged, ${phase.prs.open} open\n`;
              report += `- **Contributors**: ${Array.from(phase.contributors).join(', ') || 'None assigned'}\n`;

              if (phase.startDate && phase.endDate) {
                const duration = Math.round((phase.endDate - phase.startDate) / (1000 * 60 * 60 * 24));
                report += `- **Duration**: ${duration} days\n`;
              } else if (phase.startDate) {
                const elapsed = Math.round((new Date() - phase.startDate) / (1000 * 60 * 60 * 24));
                report += `- **Elapsed**: ${elapsed} days (in progress)\n`;
              }

              if (phase.blockers.length > 0) {
                report += `- **Blockers**: ${phase.blockers.length} issue(s) blocked\n`;
              }

              report += '\n';
            });

            // Velocity metrics
            report += '## 📈 Velocity Metrics\n\n';

            const last7Days = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
            const last30Days = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);

            const recentlyClosed = allIssues.filter(i => 
              i.closed_at && new Date(i.closed_at) > last7Days
            ).length;

            const monthlyClosed = allIssues.filter(i => 
              i.closed_at && new Date(i.closed_at) > last30Days
            ).length;

            report += `- **Last 7 days**: ${recentlyClosed} issues closed\n`;
            report += `- **Last 30 days**: ${monthlyClosed} issues closed\n`;
            report += `- **Average velocity**: ${(monthlyClosed / 4).toFixed(1)} issues/week\n\n`;

            // Dependencies and next steps
            report += '## 🔗 Phase Dependencies\n\n';
            report += '```mermaid\n';
            report += 'graph LR\n';
            for (let i = 1; i < 12; i++) {
              const currentStatus = phases[i].issues.closed === phases[i].issues.total ? 'DONE' : 'WIP';
              const nextStatus = phases[i+1].issues.closed === phases[i+1].issues.total ? 'DONE' : 'WIP';
              report += `  P${i}[Phase ${i}: ${phases[i].name}<br/>${currentStatus}] --> P${i+1}[Phase ${i+1}: ${phases[i+1].name}<br/>${nextStatus}]\n`;
            }
            report += '```\n\n';

            // Recommendations
            report += '## 💡 Recommendations\n\n';

            if (allBlockers.length > 0) {
              report += `1. **Resolve blockers**: ${allBlockers.length} issues are currently blocked and need attention\n`;
            }

            if (currentPhase && phases[currentPhase].contributors.size < 2) {
              report += `2. **Add resources**: Phase ${currentPhase} has limited contributors\n`;
            }

            const stalePhases = Object.keys(phases).filter(p => 
              phases[p].issues.open > 0 && 
              phases[p].startDate && 
              (new Date() - phases[p].startDate) > 60 * 24 * 60 * 60 * 1000
            );

            if (stalePhases.length > 0) {
              report += `3. **Review stale phases**: Phases ${stalePhases.join(', ')} have been open for over 60 days\n`;
            }

            // Create or update report issue
            const reportIssues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              labels: 'phase-report',
              state: 'open'
            });

            if (reportIssues.data.length > 0) {
              await github.rest.issues.update({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: reportIssues.data[0].number,
                body: report
              });
            } else {
              await github.rest.issues.create({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: '📊 Build Plan Phase Report',
                body: report,
                labels: ['phase-report', 'automated']
              });
            }

            // Also save as artifact
            fs.writeFileSync('phase-report.md', report);

      - name: Upload report artifact
        uses: actions/upload-artifact@v4
        with:
          name: phase-reports
          path: |
            phase-report.md
          retention-days: 90

      - name: Create burndown data
        uses: actions/github-script@v7
        with:
          script: |
            const fs = require('fs');

            // Generate burndown chart data
            const allIssues = await github.paginate(github.rest.issues.listForRepo, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Group by date
            const burndownData = {};
            const startDate = new Date('2024-01-01');
            const today = new Date();

            // Initialize data points
            for (let d = new Date(startDate); d <= today; d.setDate(d.getDate() + 1)) {
              const dateStr = d.toISOString().split('T')[0];
              burndownData[dateStr] = { open: 0, closed: 0, total: 0 };
            }

            // Process issues
            allIssues.forEach(issue => {
              const created = new Date(issue.created_at);
              const closed = issue.closed_at ? new Date(issue.closed_at) : null;

              for (let d = new Date(Math.max(created, startDate)); d <= today; d.setDate(d.getDate() + 1)) {
                const dateStr = d.toISOString().split('T')[0];
                burndownData[dateStr].total++;

                if (!closed || d < closed) {
                  burndownData[dateStr].open++;
                } else {
                  burndownData[dateStr].closed++;
                }
              }
            });

            // Generate CSV
            let csv = 'Date,Total,Open,Closed,Remaining\n';
            Object.keys(burndownData).forEach(date => {
              const data = burndownData[date];
              csv += `${date},${data.total},${data.open},${data.closed},${data.total - data.closed}\n`;
            });

            fs.writeFileSync('burndown.csv', csv);

      - name: Commit reports to repository
        run: |
          mkdir -p reports
          mv phase-report.md reports/
          mv burndown.csv reports/

          git config --local user.email "action@github.com"
          git config --local user.name "GitHub Action"

          git add reports/
          git diff --quiet && git diff --staged --quiet || (git commit -m "chore: Update phase reports [skip ci]" && git push)

toolUseResult.content:
name: Phase Reports and Analytics

on:
  schedule:
    - cron: '0 9 * * 1'  # Weekly on Monday at 9 AM UTC
  workflow_dispatch:
    inputs:
      report_type:
        description: 'Type of report to generate'
        required: true
        type: choice
        options:
          - 'phase-summary'
          - 'burndown-chart'
          - 'dependency-map'
          - 'risk-assessment'
          - 'team-velocity'

permissions:
  contents: write
  issues: write
  pull-requests: read

jobs:
  generate-reports:
    runs-on: ubuntu-latest

    steps:
      - name: Checkout code
        uses: actions/checkout@v4

      - name: Setup Node.js
        uses: actions/setup-node@v4
        with:
          node-version: '20'

      - name: Generate Phase Summary Report
        uses: actions/github-script@v7
        with:
          script: |
            const fs = require('fs');

            // Fetch all issues
            const allIssues = await github.paginate(github.rest.issues.listForRepo, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Fetch all PRs
            const allPRs = await github.paginate(github.rest.pulls.list, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Initialize phase data
            const phases = {};
            const phaseNames = [
              'Planning', 'Architecture', 'Setup', 'Core Development',
              'Integration', 'Testing', 'Optimization', 'Documentation',
              'Deployment', 'Monitoring', 'Maintenance', 'Review'
            ];

            for (let i = 1; i <= 12; i++) {
              phases[i] = {
                name: phaseNames[i-1],
                issues: { total: 0, open: 0, closed: 0 },
                prs: { total: 0, open: 0, merged: 0 },
                blockers: [],
                risks: [],
                contributors: new Set(),
                startDate: null,
                endDate: null,
                duration: 0
              };
            }

            // Process issues
            allIssues.forEach(issue => {
              issue.labels.forEach(label => {
                if (label.name.startsWith('phase-')) {
                  const phaseNum = parseInt(label.name.match(/phase-(\d+)/)[1]);
                  phases[phaseNum].issues.total++;

                  if (issue.state === 'open') {
                    phases[phaseNum].issues.open++;

                    // Check for blockers
                    if (issue.labels.some(l => l.name === 'status-blocked')) {
                      phases[phaseNum].blockers.push({
                        number: issue.number,
                        title: issue.title,
                        url: issue.html_url
                      });
                    }

                    // Check for high priority issues (risks)
                    if (issue.labels.some(l => l.name === 'priority-high' || l.name === 'priority-critical')) {
                      phases[phaseNum].risks.push({
                        number: issue.number,
                        title: issue.title,
                        priority: issue.labels.find(l => l.name.startsWith('priority-')).name,
                        url: issue.html_url
                      });
                    }
                  } else {
                    phases[phaseNum].issues.closed++;
                  }

                  // Track contributors
                  if (issue.assignees) {
                    issue.assignees.forEach(assignee => {
                      phases[phaseNum].contributors.add(assignee.login);
                    });
                  }

                  // Track dates
                  const issueDate = new Date(issue.created_at);
                  if (!phases[phaseNum].startDate || issueDate < phases[phaseNum].startDate) {
                    phases[phaseNum].startDate = issueDate;
                  }
                  if (issue.closed_at) {
                    const closeDate = new Date(issue.closed_at);
                    if (!phases[phaseNum].endDate || closeDate > phases[phaseNum].endDate) {
                      phases[phaseNum].endDate = closeDate;
                    }
                  }
                }
              });
            });

            // Process PRs
            allPRs.forEach(pr => {
              pr.labels.forEach(label => {
                if (label.name.startsWith('phase-')) {
                  const phaseNum = parseInt(label.name.match(/phase-(\d+)/)[1]);
                  phases[phaseNum].prs.total++;

                  if (pr.state === 'open') {
                    phases[phaseNum].prs.open++;
                  } else if (pr.merged_at) {
                    phases[phaseNum].prs.merged++;
                  }
                }
              });
            });

            // Generate markdown report
            let report = '# 📊 Master Workflow Build Plan - Phase Report\n\n';
            report += `*Generated: ${new Date().toLocaleString()}*\n\n`;
            report += '## Executive Summary\n\n';

            // Calculate overall progress
            let totalIssues = 0, closedIssues = 0;
            let currentPhase = 0;

            Object.keys(phases).forEach(phaseNum => {
              const phase = phases[phaseNum];
              totalIssues += phase.issues.total;
              closedIssues += phase.issues.closed;

              if (phase.issues.total > 0 && phase.issues.closed < phase.issues.total) {
                if (currentPhase === 0) currentPhase = phaseNum;
              }
            });

            const overallProgress = totalIssues > 0 ? Math.round((closedIssues / totalIssues) * 100) : 0;

            report += `- **Overall Progress**: ${closedIssues}/${totalIssues} issues (${overallProgress}%)\n`;
            report += `- **Current Phase**: Phase ${currentPhase || 12} - ${phases[currentPhase || 12].name}\n`;
            report += `- **Active Contributors**: ${new Set([...Object.values(phases).flatMap(p => Array.from(p.contributors))]).size}\n\n`;

            // Risk summary
            const allRisks = Object.values(phases).flatMap(p => p.risks);
            const allBlockers = Object.values(phases).flatMap(p => p.blockers);

            if (allRisks.length > 0 || allBlockers.length > 0) {
              report += '## ⚠️ Risks and Blockers\n\n';

              if (allBlockers.length > 0) {
                report += '### 🚧 Current Blockers\n';
                allBlockers.forEach(blocker => {
                  report += `- [#${blocker.number}](${blocker.url}): ${blocker.title}\n`;
                });
                report += '\n';
              }

              if (allRisks.length > 0) {
                report += '### 🔴 High Priority Issues\n';
                allRisks.forEach(risk => {
                  report += `- [#${risk.number}](${risk.url}): ${risk.title} (${risk.priority})\n`;
                });
                report += '\n';
              }
            }

            // Detailed phase breakdown
            report += '## Phase Details\n\n';

            Object.keys(phases).forEach(phaseNum => {
              const phase = phases[phaseNum];
              const progress = phase.issues.total > 0 ? Math.round((phase.issues.closed / phase.issues.total) * 100) : 0;
              const status = progress === 100 ? '✅' : progress > 50 ? '🔄' : progress > 0 ? '🟡' : '⏳';

              report += `### ${status} Phase ${phaseNum}: ${phase.name}\n\n`;
              report += `- **Progress**: ${phase.issues.closed}/${phase.issues.total} issues (${progress}%)\n`;
              report += `- **Pull Requests**: ${phase.prs.merged} merged, ${phase.prs.open} open\n`;
              report += `- **Contributors**: ${Array.from(phase.contributors).join(', ') || 'None assigned'}\n`;

              if (phase.startDate && phase.endDate) {
                const duration = Math.round((phase.endDate - phase.startDate) / (1000 * 60 * 60 * 24));
                report += `- **Duration**: ${duration} days\n`;
              } else if (phase.startDate) {
                const elapsed = Math.round((new Date() - phase.startDate) / (1000 * 60 * 60 * 24));
                report += `- **Elapsed**: ${elapsed} days (in progress)\n`;
              }

              if (phase.blockers.length > 0) {
                report += `- **Blockers**: ${phase.blockers.length} issue(s) blocked\n`;
              }

              report += '\n';
            });

            // Velocity metrics
            report += '## 📈 Velocity Metrics\n\n';

            const last7Days = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
            const last30Days = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);

            const recentlyClosed = allIssues.filter(i => 
              i.closed_at && new Date(i.closed_at) > last7Days
            ).length;

            const monthlyClosed = allIssues.filter(i => 
              i.closed_at && new Date(i.closed_at) > last30Days
            ).length;

            report += `- **Last 7 days**: ${recentlyClosed} issues closed\n`;
            report += `- **Last 30 days**: ${monthlyClosed} issues closed\n`;
            report += `- **Average velocity**: ${(monthlyClosed / 4).toFixed(1)} issues/week\n\n`;

            // Dependencies and next steps
            report += '## 🔗 Phase Dependencies\n\n';
            report += '```mermaid\n';
            report += 'graph LR\n';
            for (let i = 1; i < 12; i++) {
              const currentStatus = phases[i].issues.closed === phases[i].issues.total ? 'DONE' : 'WIP';
              const nextStatus = phases[i+1].issues.closed === phases[i+1].issues.total ? 'DONE' : 'WIP';
              report += `  P${i}[Phase ${i}: ${phases[i].name}<br/>${currentStatus}] --> P${i+1}[Phase ${i+1}: ${phases[i+1].name}<br/>${nextStatus}]\n`;
            }
            report += '```\n\n';

            // Recommendations
            report += '## 💡 Recommendations\n\n';

            if (allBlockers.length > 0) {
              report += `1. **Resolve blockers**: ${allBlockers.length} issues are currently blocked and need attention\n`;
            }

            if (currentPhase && phases[currentPhase].contributors.size < 2) {
              report += `2. **Add resources**: Phase ${currentPhase} has limited contributors\n`;
            }

            const stalePhases = Object.keys(phases).filter(p => 
              phases[p].issues.open > 0 && 
              phases[p].startDate && 
              (new Date() - phases[p].startDate) > 60 * 24 * 60 * 60 * 1000
            );

            if (stalePhases.length > 0) {
              report += `3. **Review stale phases**: Phases ${stalePhases.join(', ')} have been open for over 60 days\n`;
            }

            // Create or update report issue
            const reportIssues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              labels: 'phase-report',
              state: 'open'
            });

            if (reportIssues.data.length > 0) {
              await github.rest.issues.update({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: reportIssues.data[0].number,
                body: report
              });
            } else {
              await github.rest.issues.create({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: '📊 Build Plan Phase Report',
                body: report,
                labels: ['phase-report', 'automated']
              });
            }

            // Also save as artifact
            fs.writeFileSync('phase-report.md', report);

      - name: Upload report artifact
        uses: actions/upload-artifact@v4
        with:
          name: phase-reports
          path: |
            phase-report.md
          retention-days: 90

      - name: Create burndown data
        uses: actions/github-script@v7
        with:
          script: |
            const fs = require('fs');

            // Generate burndown chart data
            const allIssues = await github.paginate(github.rest.issues.listForRepo, {
              owner: context.repo.owner,
              repo: context.repo.repo,
              state: 'all',
              per_page: 100
            });

            // Group by date
            const burndownData = {};
            const startDate = new Date('2024-01-01');
            const today = new Date();

            // Initialize data points
            for (let d = new Date(startDate); d <= today; d.setDate(d.getDate() + 1)) {
              const dateStr = d.toISOString().split('T')[0];
              burndownData[dateStr] = { open: 0, closed: 0, total: 0 };
            }

            // Process issues
            allIssues.forEach(issue => {
              const created = new Date(issue.created_at);
              const closed = issue.closed_at ? new Date(issue.closed_at) : null;

              for (let d = new Date(Math.max(created, startDate)); d <= today; d.setDate(d.getDate() + 1)) {
                const dateStr = d.toISOString().split('T')[0];
                burndownData[dateStr].total++;

                if (!closed || d < closed) {
                  burndownData[dateStr].open++;
                } else {
                  burndownData[dateStr].closed++;
                }
              }
            });

            // Generate CSV
            let csv = 'Date,Total,Open,Closed,Remaining\n';
            Object.keys(burndownData).forEach(date => {
              const data = burndownData[date];
              csv += `${date},${data.total},${data.open},${data.closed},${data.total - data.closed}\n`;
            });

            fs.writeFileSync('burndown.csv', csv);

      - name: Commit reports to repository
        run: |
          mkdir -p reports
          mv phase-report.md reports/
          mv burndown.csv reports/

          git config --local user.email "action@github.com"
          git config --local user.name "GitHub Action"

          git add reports/
          git diff --quiet && git diff --staged --quiet || (git commit -m "chore: Update phase reports [skip ci]" && git push)

